public class pre_and_post_increment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 10;
		System.out.println(num++);
		System.out.println(num);
		System.out.println("-------------");
		System.out.println(++num);
		//System.out.println(num);

	
	}

}
